import sysimport jsonfrom bson.objectid import ObjectIdfrom pymongo import MongoClientclass MongoCall(object):    def __init__(self, table):        self.connection = MongoClient("localhost",27017)        self.table = table    def get(self):        documents = self.connection.blog[self.table].find()        return documents    def getByID(self, object_id):        documents = self.connection.blog[self.table].find({"_id": ObjectId(object_id)})        return documents    def getByQuery(self, query):        documents = self.connection.blog[self.table].find(query)        return documents    def insert(self, document):        return self.connection.blog[self.table].insert_one(document)    def delete(self, object_id):        return self.connection.blog[self.table].delete_one({'_id': ObjectId(object_id)})    def updateEntireDocument(self, object_id, document):        return self.connection.blog[self.table].update({'_id': ObjectId(object_id)}, {"$set": document})class ArticleCall(MongoCall):    def __init__(self):        MongoCall.__init__(self, "articles")class UserCall(MongoCall):    def __init__(self):        MongoCall.__init__(self, "users")class CategoryCall(MongoCall):    def __init__(self):        MongoCall.__init__(self, "categories")